import React, { useState } from "react";
import "./App.css";

const App: React.FC = () => {
  const [sessionId, setSessionId] = useState<string>("");

  const handleCreateSession = () => {
    // Placeholder for session creation logic
    alert("Session Created!");
  };

  const handleJoinSession = () => {
    if (!sessionId) {
      alert("Please enter a session ID!");
      return;
    }
    alert(`Joined session: ${sessionId}`);
  };

  const handleSync = () => {
    // Placeholder for sync logic
    alert("Synchronizing playback...");
  };

  return (
    <div className="syncwatch-container">
      <header className="syncwatch-header">
        <h1>SyncWatch</h1>
      </header>
      <main className="syncwatch-main">
        <div className="session-controls">
          <button className="btn" onClick={handleCreateSession}>
            Create Session
          </button>
          <div className="join-session">
            <input
              type="text"
              placeholder="Enter Session ID"
              value={sessionId}
              onChange={(e) => setSessionId(e.target.value)}
            />
            <button className="btn" onClick={handleJoinSession}>
              Join Session
            </button>
          </div>
        </div>
        <div className="playback-controls">
          <button className="btn btn-play" onClick={() => alert("Play")}>
            ‚ñ∂Ô∏è Play
          </button>
          <button className="btn btn-pause" onClick={() => alert("Pause")}>
            ‚è∏ Pause
          </button>
          <button className="btn btn-sync" onClick={handleSync}>
            üîÑ Sync
          </button>
          <button className="btn btn-skip" onClick={() => alert("Skip")}>
            ‚è© Skip
          </button>
        </div>
      </main>
    </div>
  );
};

export default App;
